import { useState } from "react";
import { makeRequest } from "../../axios";
import "./update.css";
import  { useMutation, useQueryClient } from '@tanstack/react-query';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';


import React from 'react'

const Update = ({setOpenUpdate,user}) => {

    const [cover, setCover] = useState(null);
    const [profile, setProfile] = useState(null);
    const [texts, setTexts] = useState({
        email: user.email,
        password: user.password,
        name: user.name,
        city: user.city,
        
        // name: "",
        // city: ""
      });

      const upload = async (file) => {
        console.log(file)
        try {
          const formData = new FormData();
          formData.append("file", file);
          const res = await makeRequest.post("/upload", formData);
          return res.data;
        } catch (err) {
          console.log(err);
        }
      };

      const handleChange = (e) => {
        setTexts((prev) => ({ ...prev, [e.target.name]: [e.target.value] }));
      };

      const queryClient = useQueryClient();

      const mutation = useMutation(
        (user) => {
        return makeRequest.put("/users", user);
        },
        {
        onSuccess: () => {
            // Invalidate and refetch
            queryClient.invalidateQueries(["user"]);
        },
        }
      );

      const handleClick = async (e) => {
        e.preventDefault();

        let coverUrl;
        let profileUrl;
        coverUrl = cover ? await upload(cover) : user.coverPic;
        profileUrl = profile ? await upload(profile) : user.profilePic;
        
        mutation.mutate({ ...texts, coverPic: coverUrl, profilePic: profileUrl });
        setOpenUpdate(false);
        setCover(null);
        setProfile(null);
      }




  return (
    <div className="update">
        <div className="wrapper">
        <h1 className="profupdate">Update Your Profile</h1>
        <form className="profForm">
          <div className="files">
            <label className="profLabel" htmlFor="cover">
              <span>Cover Picture</span>
              <div className="imgContainer">
                <img className="ownImg"
                  src={
                    cover
                      ? URL.createObjectURL(cover)
                      : "/upload/" + user.coverPic
                  }
                  alt=""
                />
                <CloudUploadIcon className="icon" />
              </div>
            </label>
            <input className="detailsInput"
              type="file"
              id="cover"
              style={{ display: "none" }}
              onChange={(e) => setCover(e.target.files[0])}
            />
            <label htmlFor="profile">
              <span>Profile Picture</span>
              <div className="imgContainer">
                <img
                  src={
                    profile
                      ? URL.createObjectURL(profile)
                      : "/upload/" + user.profilePic
                  }
                  alt=""
                />
                <CloudUploadIcon className="icon" />
              </div>
            </label>
            <input className="detailsInput"
              type="file"
              id="profile"
              style={{ display: "none" }}
              onChange={(e) => setProfile(e.target.files[0])}
            />
          </div>
          <label>Email</label>
          <input className="detailsInput"
            type="text"
            value={texts.email}
            name="email"
            onChange={handleChange}
          />
          <label>Password</label>
          <input className="detailsInput"
            type="text"
            value={texts.password}
            name="password"
            onChange={handleChange}
          />
          <label>Name</label>
          <input className="detailsInput"
            type="text"
            value={texts.name}
            name="name"
            onChange={handleChange}
          />
          <label>Country / City</label>
          <input className="detailsInput"
            type="text"
            name="city"
            value={texts.city}
            onChange={handleChange}
          />
          
          <button className="buttonClose" onClick={handleClick}>Update</button>
        </form>
        <button className="close" onClick={() => setOpenUpdate(false)}>
          Close
        </button>
      </div>


      

      {/* <form>
        <input type="file" onChange={e=>setCover(e.target.files[0])}/>
        <input type="file" onChange={e=>setProfile(e.target.files[0])}/>
        <input type="text" name="name" onChange={handleChange}/>
        <input type="text" name="city" onChange={handleChange} />
        <button onClick={handleClick}>Update</button>
      </form> */}
      {/* <button onClick={()=>setOpenUpdate(false)}>X</button> */}
    </div>
  )

}

export default Update;
