import "./game.css"
import React, { useState } from 'react'

const Game = () => {
    const [gamecards] = useState([
        {
            title: 'Parachute Games',
            pic: 'https://picsum.photos/200/300',
            text: 'Specially designed parachutes can be bought in different sizes to play parachute games. Reinforced handles around the edge of the parachute make it easier for participants with poor grip or hand control to hold on to. Try these games with the children standing in a circle holding the parachute by the outside edges: Making waves - An adult directs the children to make small or large waves by moving their arms up and down.  Slow to start then faster and then slow.  Introduce a story about the sea or the wind on a stormy night.Rolling the ball - An adult places a ball on the parachute.  The children move the parachute up and down to stop the ball falling through the centre hole.  Vary it with different sized balls.Shaking Popcorn:  Place some beanbags on the parachute. The children shake the chute to make them rise like popcorn.'
        },
        {
            title: 'The Autism Show',
            text: 'The Autism Show on April 2nd is an event focused on autism, offering information, resources, and support for individuals, families, and professionals.'
        },
        {
            title: 'Inclusion Symposium',
            text: 'On March 3rd, International School Dhaka (ISD) in Bangladesh will host the inaugural Inclusion Symposium, focusing on diversity and inclusion in child special needs education.'
        },
        {
            title: 'Rise & Shine',
            text: 'Rise & Shine on April 2nd is a special event dedicated to children with autism, providing a platform for them to shine and thrive, and showcase their abilities to the world.'
        },
        {
            title: 'Inclusion Symposium',
            text: 'On March 3rd, International School Dhaka (ISD) in Bangladesh will host the inaugural Inclusion Symposium, focusing on diversity and inclusion in child special needs education.'
        },
        {
            title: 'Rise & Shine',
            text: 'Rise & Shine on April 2nd is a special event dedicated to children with autism, providing a platform for them to shine and thrive, and showcase their abilities to the world.'
        }
    ])
  
  
  
  
  return (
    <div className="game">
      <section className="gsec">
        <div className="gamecontainer">
            <h1 className="gamename">Games For Your Child</h1>
            <div className="gamecards">
                {
                    gamecards.map((gcard, i) => (

                        <div key={i} className="gamecard">
                            <h3 className="gamecardname">{gcard.title}</h3>
                            {/* <img className="piccard" src={card.pic} alt="" /> */}
                            <span className="gameptext">{gcard.text}</span>
                            {/* <button className="gamecardbtn">Explore</button> */}
                        </div>

                    ))
                }
                
            </div>

        </div>
      </section>
    </div>
  )
}

export default Game
